import { StyleSheet, Text, View } from 'react-native';
import React, { useEffect } from 'react';
import Plant from './Plant';
import { useState } from 'react';
import { getPlants, getUser } from '../utils/service';

const Hibernacle = (props) => {
  console.log('Hibernacle()');
  const [plants, setPlants] = useState([]);
  const [user, setUser] = useState(null);

  const getData = async () => {
    try {
      const data = await getUser(8);
      setUser(data.name);
      setPlants(data.plantsArray);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getData();
    // console.log('fetching data');
  }, []);

  return (
    <View style={styles.hibernacleContainer}>
      <View>
        <Text style={styles.hibernacle}>{user}'s Hibernacle</Text>
      </View>
      <View
        style={{
          flex: 1,
          width: 360,
          height: 600,
          flexDirection: 'row',
          flexWrap: 'wrap',
          alignItems: 'flex-start',
          justifyContent: 'space-evenly',
          borderWidth: 3,
          borderColor: 'purple',
          marginTop: 20,
          backgroundColor: 'rgb(243,242,238)',
          borderRadius: 20,
        }}
      >
        {/* <Text>Hello World</Text>
        <Text>{JSON.stringify(plants)}</Text> */}
        {plants.map((element, index) => (
          <Plant plant={element} key={index} />
        ))}
      </View>
    </View>
  );
};

export default Hibernacle;

const styles = StyleSheet.create({
  hibernacle: {
    marginTop: 10,
    fontSize: 16,
    fontFamily: 'lato',
    color: '#009c97',
  },
  hibernacleContainer: {
    marginTop: 30,
    flex: 1,
    width: 395,
    justifyContent: 'center',
    alignItems: 'center',

    // widht: 600,
  },
});
