import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Image,
  Button,
  Alert,
} from 'react-native';
import { firebase } from '../utils/config';
import * as ImagePicker from 'expo-image-picker';
import SearchBar from '../components/SearchBar';
import { addPlantToUser } from '../utils/service';
import { SliderPicker } from 'react-native-slider-picker';

function AddPlant() {
  const [nickNameText, onChangeNickNameText] = React.useState('');
  const [plantImgURI, setPlantImgURI] = React.useState(null);
  const [uploading, setUploading] = useState(false);
  const [plantUri, setPlantUri] = useState(null);
  const [plantObject, setPlantObject] = useState(null);
  const [waterReminder, setWaterReminder] = useState(5);

  const submitPlant = () => {
    const plantSchema = {
      ...plantObject,
      imagePath: plantUri,
      nickName: nickNameText,
      wateringReminderInterval: waterReminder,
      nextReminderDate: Date.now() + waterReminder * 1000 * 60 * 60 * 24,
    };
    console.log(plantSchema);
    addPlantToUser(plantSchema);
    // TODO set the plantUri to null once the info has been saved into the backend
    //setPlantUri(null);
    //setPlantObject({});
    //setWaterReminder(null);
  };

  const hasMediaLibraryPermissionGranted = async () => {
    let granted = false;
    const permission = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (!permission.canAskAgain || permission.status === 'denied') {
      granted = false;
    }
    if (permission.granted) {
      granted = true;
    }
    return granted;
  };
  const pickImage = async () => {
    const storagePermissionGranted = await hasMediaLibraryPermissionGranted();
    if (!storagePermissionGranted) return plantImgURI;
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [3, 4],
      quality: 1,
    });
    if (!result.cancelled) {
      const source = { uri: result.uri };
      setPlantImgURI(source);
    }
    return plantImgURI;
  };

  const uploadImage = async () => {
    setUploading(true);
    const response = await fetch(plantImgURI.uri);
    const blob = await response.blob();
    const filename = plantImgURI.uri.substring(
      plantImgURI.uri.lastIndexOf('/') + 1
    );
    try {
      // const storageRef = firebase.storage.ref();
      // const file = storageRef.child(filename);

      let snapshot = await firebase.storage().ref().child(filename).put(blob);
      let url = await snapshot.ref.getDownloadURL();
      console.log(url, 'url');
      setUploading(false);
      Alert.alert('Your photo has been updated');
      setPlantUri(url);
      setPlantImgURI(null);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text>Add plant to your hibernacle</Text>
      <TextInput
        round
        placeholder="Optional plant's nickname"
        onChangeText={onChangeNickNameText}
        value={nickNameText}
        style={styles.input}
        // key={key.id}
      ></TextInput>
      <SearchBar
        setPlantObject={setPlantObject}
        // key={key.id}
      />
      <View style={styles.uploadimage}>
        <Pressable
          title="Pick an image from camera roll"
          onPress={pickImage}
          style={styles.imgButton}
        >
          <Text>Add a photo from your gallery</Text>
        </Pressable>
        {plantImgURI && (
          <Image source={{ uri: plantImgURI.uri }} style={styles.plantImage} />
        )}
        <Pressable onPress={uploadImage}>
          <Text>Upload Image</Text>
        </Pressable>
      </View>
      <View>
        {plantObject && (
          <Text>
            {plantObject.nickName ? plantObject.nickname : plantObject.latin}
            Watering advice: {plantObject.watering}
          </Text>
        )}
      </View>
      <SliderPicker
        minLabel={'0'}
        midLabel={'7'}
        maxLabel={'15'}
        maxValue={15}
        onChange={(position) => setWaterReminder(position)}
        defaultValue={waterReminder}
        labelFontColor={'#6c7682'}
        labelFontWeight={'400'}
        // showFill={true}
        fillColor={'rgb(151,179,128)'}
        // // labelFontWeight={'bold'}
        showNumberScale={true}
        // showSeparatorScale={true}
        // buttonBackgroundColor={'#fff'}
        // buttonBorderColor={'#6c7682'}
        buttonBorderWidth={2}
        scaleNumberFontWeight={'100'}
        // buttonDimensionsPercentage={6}
        // heightPercentage={1}
        // widthPercentage={80}
      />

      <Pressable onPress={submitPlant} style={styles.addplant}>
        <Text>ADD PLANT</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  imgButton: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 220,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: { alignItems: 'center', justifyContent: 'center', flex: 1 },
  searchBar: {
    borderRadius: 80,
    height: 40,
    width: 200,
    fontSize: 12,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
  },
  plantImage: {
    height: 213,
    width: 160,
    borderWidth: 1,
    padding: 5,
    marginTop: 10,
    borderRadius: 60,
  },
  addplant: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 120,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  uploadimage: { alignItems: 'center', justifyContent: 'center' },
});
export default AddPlant;
