import {
  StyleSheet,
  Text,
  View,
  TextInput,
  FlatList,
  TouchableOpacity,
  VariantsBox,
} from 'react-native';
import React from 'react';
import { useState, useEffect } from 'react';
import { getPlants } from '../utils/service';
import SelectDropdown from 'react-native-select-dropdown';

const Item = (props) => {
  console.log('item()', props);
  return (
    <View>
      <Text>{props.family}</Text>
    </View>
  );
};
const PlantSearchBar = (props) => {
  const [search, setSearch] = useState('');
  const [filteredDataSource, setFilteredDataSource] = useState([]);
  const [masterDataSource, setMasterDataSource] = useState([]);

  const data = async () => {
    try {
      const filteredData = await getPlants();
      setFilteredDataSource(filteredData);
      setMasterDataSource(filteredData);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    data();
  }, []);
  const searchFilterFunction = (text) => {
    console.log('text', text);
    if (text) {
      console.log(masterDataSource);
      const newData = masterDataSource.filter(function (item) {
        const itemData = item.common[0]
          ? item.common[0].toLowerCase()
          : ''.toLowerCase();
        const textData = text.toLowerCase();
        return itemData.indexOf(textData) > -1;
      });
      console.log('filteredData', newData);
      setFilteredDataSource(newData);
      setSearch(text);
    } else {
      setFilteredDataSource(masterDataSource);
      setSearch(text);
    }
  };
  const onBankSelected = (value) => {
    // setBankName(value);
    setFilteredDataSource([]);
  };
  const renderItem = ({ item }) => <Item family={item.family} />;

  const _renderItem = (item) => {
    return (
      <View style={styles.item}>
        <Text>{item.label}</Text>
      </View>
    );
  };

  return (
    <View>
      <TextInput
        round
        style={styles.searchBar}
        onChangeText={(text) => searchFilterFunction(text)}
        onClear={(text) => searchFilterFunction('')}
        placeholder="Search plant by it's common name"
        value={search}
      />
      <SelectDropdown
        data={filteredDataSource}
        onSelect={(selectedItem, index) => {
          console.log('hi Dan!');
        }}
        defaultButtonText={'Search'}
        // defaultButtonText={'⬇️'}
        buttonTextAfterSelection={(selectedItem, index) => {
          return selectedItem.common[0];
        }}
        rowTextForSelection={(item, index) => {
          return item.common[0];
        }}
        // buttonStyle={{
        //   height: 40,
        //   // width: dropdown ? 120 : 60,
        //   marginTop: 10,
        //   width: 200,
        //   borderRadius: 1000,
        //   backgroundColor: '#ECECEC',
        // }}
        dropdownBackgroundColor="#ECECEC"
      />
    </View>
  );
};

export default PlantSearchBar;

const styles = StyleSheet.create({
  searchBar: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
    width: 260,
  },
  icon: {
    marginRight: 5,
    width: 18,
    height: 18,
  },
});
