import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Image,
} from 'react-native';
import SearchBar from '../components/SearchBar';

function AddPlant() {
  const [nickNameText, onChangeNickNameText] = React.useState('');
  const [plantImgURL, setPlantImgURL] = React.useState('');
  const submitPlant = () => {};

  return (
    <View style={styles.container}>
      <Text>Add plant to your hibernacle</Text>
      <TextInput
        round
        placeholder="Optional plant's nickname"
        onChangeText={onChangeNickNameText}
        value={nickNameText}
        style={styles.input}
        // key={key.id}
      ></TextInput>
      <SearchBar
      // key={key.id}
      />
      <Image
        style={styles.plantImage}
        source={{
          uri: plantImgURL,
        }}
      />
      <Pressable onPress={submitPlant} style={styles.addplant}>
        <Text>ADD PLANT</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { alignItems: 'center', justifyContent: 'center', flex: 1 },
  searchBar: {
    borderRadius: 80,
    height: 40,
    width: 200,
    fontSize: 12,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
  },
  plantImage: {
    height: 160,
    width: 160,
    borderWidth: 1,
    padding: 5,
    marginTop: 10,
    borderRadius: 60,
  },
  addplant: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
  },
});
export default AddPlant;
