import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ImageBackground } from 'react-native';

import React from 'react';
import HomeScreen from './screens/home.screen';
import AddPlant from './screens/addPlant.screen';
import FindPlant from './screens/find.screen';
import WaterReminder from './screens/waterRemind.screen';

import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import { SafeAreaProvider } from 'react-native-safe-area-context';

import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';

const Tab = createMaterialBottomTabNavigator();

export default function App() {
  return (
    <SafeAreaProvider>
      <NavigationContainer>
        <Tab.Navigator>
          <Tab.Screen
            name="Home"
            component={HomeScreen}
            options={{
              tabBarIcon: () => {
                return <MaterialCommunityIcons name="home-circle" size={32} />;
              },
            }}
          />
          <Tab.Screen
            name="addPlant"
            component={AddPlant}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="plus-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          />
          <Tab.Screen
            name="find"
            component={FindPlant}
            options={{
              tabBarIcon: () => {
                return <MaterialCommunityIcons name="magnify" size={32} />;
              },
            }}
          />
          <Tab.Screen
            name="waterReminder"
            component={WaterReminder}
            options={{
              tabBarIcon: () => {
                return <MaterialCommunityIcons name="water-circle" size={32} />;
              },
            }}
          />
        </Tab.Navigator>
      </NavigationContainer>
    </SafeAreaProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'beige',
    justifyContent: 'space-between',
  },
  hibernacle: {
    flex: 1,
  },
  botBar: {
    height: 100,
  },
});
