import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import React from 'react';
import HomeScreen from './screens/home.screen';
import AddPlant from './screens/addPlant.screen';
import FindPlant from './screens/find.screen';
import WaterReminder from './screens/waterRemind.screen';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
// import { initializeApp } from 'https://www.gstatic.com/firebasejs/9.8.3/firebase-firestore.js';
// import * as firebase from 'firebase/app';
// import { initializeApp } from 'firebase/app';
// import { getFirestore } from 'firebase/firestore/lite';

const firebaseConfig = {
  apiKey: 'AIzaSyAwm4JhUIKnJeO4qOjXJbzRKFiLEXH1d58',
  authDomain: 'vaterra-22740.firebaseapp.com',
  projectId: 'vaterra-22740',
  storageBucket: 'vaterra-22740.appspot.com',
  messagingSenderId: '901541040609',
  appId: '1:901541040609:web:c46076836268d4da9082a0',
};
// const app = firebase.initializeApp(firebaseConfig);
// const db = getFirestore(app);
const Tab = createMaterialBottomTabNavigator();

export default function App() {
  return (
    <SafeAreaProvider>
      <NavigationContainer>
        <Tab.Navigator
          labeled={false}
          barStyle={{ backgroundColor: 'rgb(151,179,128)' }}
          activeColor="white"
        >
          <Tab.Screen
            name="Home"
            component={HomeScreen}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="home-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          />
          <Tab.Screen
            name="addPlant"
            component={AddPlant}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="plus-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          />
          <Tab.Screen
            name="find"
            component={FindPlant}
            options={{
              tabBarIcon: () => {
                return <MaterialCommunityIcons name="magnify" size={32} />;
              },
            }}
          />
          <Tab.Screen
            name="waterReminder"
            component={WaterReminder}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons name="water-outline" size={32} />
                );
              },
            }}
          />
        </Tab.Navigator>
      </NavigationContainer>
    </SafeAreaProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'beige',
    justifyContent: 'space-between',
  },
});
