import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import IntervalSliders from '../components/IntervalSliders';
import { getUser } from '../utils/service';

function WaterRemind() {
  const [plants, setPlants] = useState([]);

  const getData = async () => {
    try {
      const data = await getUser(8);
      console.log(data);
      setPlants(data.plantsArray);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getData();
    // console.log('fetching data');
  }, []);
  return (
    <View style={styles.slidersContainer}>
      <View style={{ alignItems: 'center', justifyContent: 'center', flex: 1 }}>
        <Text>WATERING REMINDERS</Text>
      </View>
      <View>
        {plants.map((element, index) => (
          <IntervalSliders plant={element} key={index} />
        ))}
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  slidersContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  text: {
    marginTop: 10,
    fontSize: 23,
    fontWeight: 'bold',
    fontFamily: 'Roboto',
    color: '#009c97',
    letterSpacing: 8,
  },
});
export default WaterRemind;
