import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Image,
  Button,
  Alert,
} from 'react-native';
import { firebase } from '../utils/config';
import * as ImagePicker from 'expo-image-picker';
import SearchBar from '../components/SearchBar';
import { addPlantToUser } from '../utils/service';
import Slider from '@react-native-community/slider';
import addDaystoDate from '../utils/helperFunctions';

function AddPlant() {
  const [nickNameText, onChangeNickNameText] = React.useState('');
  const [plantImgURI, setPlantImgURI] = React.useState(null);
  const [uploading, setUploading] = useState(false);
  const [plantUri, setPlantUri] = useState(null);
  const [plantObject, setPlantObject] = useState(null);
  const [waterReminder, setWaterReminder] = useState(5);

  const submitPlant = () => {
    if (!plantUri) {
      Alert.alert(
        'please upload an Image before adding the plant to your hibernacle :)'
      );
    } else {
      if (!plantObject) {
        Alert.alert(
          'Please select a Plant before adding it to your hibernacle :)'
        );
      } else {
        const plantSchema = {
          ...plantObject,
          imagePath: plantUri,
          nickName: nickNameText,
          wateringReminderInterval: waterReminder,
          nextReminderDate: addDaystoDate(waterReminder),
        };
        console.log(plantSchema);
        addPlantToUser(plantSchema);
        Alert.alert('Your plant has been added to your hibernacle :)');
        setPlantUri(null);
        setPlantObject({});
        setWaterReminder(null);
      }
    }
  };

  const hasMediaLibraryPermissionGranted = async () => {
    let granted = false;
    const permission = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (!permission.canAskAgain || permission.status === 'denied') {
      granted = false;
    }
    if (permission.granted) {
      granted = true;
    }
    return granted;
  };
  const pickImage = async () => {
    const storagePermissionGranted = await hasMediaLibraryPermissionGranted();
    if (!storagePermissionGranted) return plantImgURI;
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [3, 4],
      quality: 1,
    });
    if (!result.cancelled) {
      const source = { uri: result.uri };
      setPlantImgURI(source);
    }
    return plantImgURI;
  };

  const uploadImage = async () => {
    setUploading(true);
    const response = await fetch(plantImgURI.uri);
    const blob = await response.blob();
    const filename = plantImgURI.uri.substring(
      plantImgURI.uri.lastIndexOf('/') + 1
    );
    try {
      let snapshot = await firebase.storage().ref().child(filename).put(blob);
      let url = await snapshot.ref.getDownloadURL();
      setUploading(false);
      Alert.alert('Your photo has been updated');
      setPlantUri(url);
      setPlantImgURI(null);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text
        style={{
          marginTop: 20,
          fontSize: 16,
          fontWeight: 'bold',
          fontFamily: 'Roboto',
          color: '#009c97',
          letterSpacing: 3,
        }}
      >
        Add a new plant to your hibernacle
      </Text>
      <TextInput
        placeholder="Add your new plant nickname"
        onChangeText={onChangeNickNameText}
        value={nickNameText}
        style={styles.input}
      ></TextInput>
      <SearchBar setPlantObject={setPlantObject} />
      <View style={styles.uploadimage}>
        <Pressable
          title="Pick an image from camera roll"
          onPress={pickImage}
          style={styles.imgButton}
        >
          <Text>Add a photo from your gallery</Text>
        </Pressable>
        {plantImgURI && (
          <Image source={{ uri: plantImgURI.uri }} style={styles.plantImage} />
        )}
        <Pressable onPress={uploadImage}>
          <Text>Upload Image</Text>
        </Pressable>
      </View>
      <View>
        {plantObject && (
          <Text>
            {plantObject.nickName ? plantObject.nickname : plantObject.latin}
            Watering advice: {plantObject.watering}
          </Text>
        )}
      </View>
      <View>
        <Text>Depending on your local weather, </Text>
        <Slider
          style={{ width: 300, height: 40 }}
          minimumValue={1}
          maximumValue={15}
          minimumTrackTintColor={'#009c97'}
          onValueChange={(value) => {
            setWaterReminder(Math.floor(value));
          }}
          thumbTintColor={'#009c97'}
        />
        <Text>{waterReminder}</Text>
      </View>
      <Pressable onPress={submitPlant} style={styles.addplant}>
        <Text>ADD PLANT</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  imgButton: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 220,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    alignItems: 'center',
    justifyContent: 'space-evenly',
    flex: 1,
    borderWidth: 2,
    borderColor: '#009c97',
  },
  searchBar: {
    borderRadius: 80,
    height: 40,
    width: 200,
    fontSize: 12,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
  },
  plantImage: {
    height: 213,
    width: 160,
    borderWidth: 1,
    padding: 5,
    marginTop: 10,
    borderRadius: 60,
  },
  addplant: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 120,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  uploadimage: { alignItems: 'center', justifyContent: 'center' },
});
export default AddPlant;
