import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import React from 'react';
import HomeScreen from './screens/home.screen';
import AddPlant from './screens/addPlant.screen';
// import FindPlant from './screens/find.screen';
import WaterReminder from './screens/waterRemind.screen';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import * as Notifications from 'expo-notifications';
import { useEffect, useState } from 'react';
import { registerForPushNotificationsAsync } from './utils/config';

const Tab = createMaterialBottomTabNavigator();

Notifications.setNotificationHandler({
  handleNotification: async () => ({
    shouldShowAlert: true,
  }),
});

export default function App() {
  const props = {
    waterReminder: 5,
  };
  const [waterReminder, setWaterReminder] = useState(5);
  useEffect(() => {
    registerForPushNotificationsAsync();
  }, []);
  return (
    <SafeAreaProvider>
      <NavigationContainer>
        <Tab.Navigator
          labeled={false}
          barStyle={{ backgroundColor: 'rgb(151,179,128)' }}
          activeColor="white"
        >
          <Tab.Screen
            name="Home"
            component={HomeScreen}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="home-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          />
          <Tab.Screen
            name="addPlant"
            // component={AddPlant}
            // waterReminder={waterReminder}
            props={props}
            setWaterReminder={setWaterReminder}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="plus-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          >
            {(props) => {
              <AddPlant {...props} waterReminder={waterReminder} />;
            }}
          </Tab.Screen>
          {/* <Tab.Screen
            name="find"
            component={FindPlant}
            options={{
              tabBarIcon: () => {
                return <MaterialCommunityIcons name="magnify" size={32} />;
              },
            }}
          /> */}
          <Tab.Screen
            name="waterReminder"
            component={WaterReminder}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons name="water-outline" size={32} />
                );
              },
            }}
          />
        </Tab.Navigator>
      </NavigationContainer>
    </SafeAreaProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'beige',
    justifyContent: 'space-between',
  },
});
