import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Image,
  Button,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import SearchBar from '../components/SearchBar';

const hasMediaLibraryPermissionGranted = async () => {
  let granted = false;
  const permission = await ImagePicker.requestMediaLibraryPermissionsAsync();
  if (!permission.canAskAgain || permission.status === 'denied') {
    granted = false;
  }
  if (permission.granted) {
    granted = true;
  }
  return granted;
};

function AddPlant() {
  const [nickNameText, onChangeNickNameText] = React.useState('');
  const [plantImgURL, setPlantImgURL] = React.useState(null);
  const [image, setImage] = useState(null);

  const submitPlant = () => {
    const plantSchema = {};
  };

  const pickImage = async () => {
    let imgURI = null;
    const storagePermissionGranted = await hasMediaLibraryPermissionGranted();
    if (!storagePermissionGranted) return imgURI;
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [3, 4],
      quality: 1,
    });
    if (!result.cancelled) {
      imgURI = result.uri;
    }
    return imgURI;
  };

  return (
    <View style={styles.container}>
      <Text>Add plant to your hibernacle</Text>
      <TextInput
        round
        placeholder="Optional plant's nickname"
        onChangeText={onChangeNickNameText}
        value={nickNameText}
        style={styles.input}
        // key={key.id}
      ></TextInput>
      <SearchBar
      // key={key.id}
      />
      <View style={styles.uploadimage}>
        <Pressable
          title="Pick an image from camera roll"
          onPress={pickImage}
          style={styles.imgButton}
        >
          <Text>Add a photo from your gallery</Text>
        </Pressable>
        {image && <Image source={{ uri: image }} style={styles.plantImage} />}
      </View>

      <Pressable onPress={submitPlant} style={styles.addplant}>
        <Text>ADD PLANT</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  imgButton: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 220,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: { alignItems: 'center', justifyContent: 'center', flex: 1 },
  searchBar: {
    borderRadius: 80,
    height: 40,
    width: 200,
    fontSize: 12,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
  },
  plantImage: {
    height: 213,
    width: 160,
    borderWidth: 1,
    padding: 5,
    marginTop: 10,
    borderRadius: 60,
  },
  addplant: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 120,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  uploadimage: { alignItems: 'center', justifyContent: 'center' },
});
export default AddPlant;
