import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import React from 'react';
import HomeScreen from './screens/home.screen';
import AddPlant from './screens/addPlant.screen';
import FindPlant from './screens/find.screen';
import WaterReminder from './screens/waterRemind.screen';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import * as Notifications from 'expo-notifications';
import { useEffect } from 'react';

const Tab = createMaterialBottomTabNavigator();

async function registerForPushNotificationsAsync() {
  let token;
  const { status: existingStatus } = await Notifications.getPermissionsAsync();
  let finalStatus = existingStatus;
  if (existingStatus !== 'granted') {
    const { status } = await Notifications.requestPermissionsAsync();
    finalStatus = status;
  }
  if (finalStatus !== 'granted') {
    alert('Failed to get push token for push notification!');
    return;
  }
  token = (await Notifications.getExpoPushTokenAsync()).data;
  console.log({ token });
  return token;
}
Notifications.setNotificationHandler({
  handleNotification: async () => ({
    shouldShowAlert: true,
  }),
});

export default function App() {
  useEffect(() => {
    registerForPushNotificationsAsync();
  }, []);
  return (
    <SafeAreaProvider>
      <NavigationContainer>
        <Tab.Navigator
          labeled={false}
          barStyle={{ backgroundColor: 'rgb(151,179,128)' }}
          activeColor="white"
        >
          <Tab.Screen
            name="Home"
            component={HomeScreen}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="home-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          />
          <Tab.Screen
            name="addPlant"
            component={AddPlant}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons
                    name="plus-circle-outline"
                    size={32}
                  />
                );
              },
            }}
          />
          <Tab.Screen
            name="find"
            component={FindPlant}
            options={{
              tabBarIcon: () => {
                return <MaterialCommunityIcons name="magnify" size={32} />;
              },
            }}
          />
          <Tab.Screen
            name="waterReminder"
            component={WaterReminder}
            options={{
              tabBarIcon: () => {
                return (
                  <MaterialCommunityIcons name="water-outline" size={32} />
                );
              },
            }}
          />
        </Tab.Navigator>
      </NavigationContainer>
    </SafeAreaProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'beige',
    justifyContent: 'space-between',
  },
});
