import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Image,
  Button,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import SearchBar from '../components/SearchBar';

function AddPlant() {
  const [nickNameText, onChangeNickNameText] = React.useState('');
  const [plantImgURL, setPlantImgURL] = React.useState(null);
  const [image, setImage] = useState(null);

  const submitPlant = () => {
    const plantSchema = {};
  };
  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage(result.uri);
    }
  };
  return (
    <View style={styles.container}>
      <Text>Add plant to your hibernacle</Text>
      <TextInput
        round
        placeholder="Optional plant's nickname"
        onChangeText={onChangeNickNameText}
        value={nickNameText}
        style={styles.input}
        // key={key.id}
      ></TextInput>
      <SearchBar
      // key={key.id}
      />
      {/* <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Button title="Pick an image from camera roll" onPress={pickImage} />
        {image && (
          <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />
        )}
      </View> */}
      {plantImgURL ? (
        <Image
          style={styles.plantImage}
          source={{
            uri: plantImgURL,
          }}
        />
      ) : (
        <Text> No Image added</Text>
      )}
      <Pressable onPress={submitPlant} style={styles.addplant}>
        <Text>ADD PLANT</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { alignItems: 'center', justifyContent: 'center', flex: 1 },
  searchBar: {
    borderRadius: 80,
    height: 40,
    width: 200,
    fontSize: 12,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
  },
  plantImage: {
    height: 160,
    width: 160,
    borderWidth: 1,
    padding: 5,
    marginTop: 10,
    borderRadius: 60,
  },
  addplant: {
    borderWidth: 1,
    backgroundColor: 'rgb(151,179,128)',
    marginTop: 20,
    borderRadius: 5,
    width: 120,
    height: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
});
export default AddPlant;
