import {
  StyleSheet,
  Text,
  View,
  TextInput,
  FlatList,
  TouchableOpacity,
} from 'react-native';
import { VariantsBox } from 'react-native-web/dist/index';
import React from 'react';
import { useState, useEffect } from 'react';
import { getPlants } from '../utils/service';

const PlantSearchBar = (props) => {
  const [search, setSearch] = useState('');
  const [filteredDataSource, setFilteredDataSource] = useState([]);
  const [masterDataSource, setMasterDataSource] = useState([]);

  const data = async () => {
    try {
      const data = await getPlants();

      setFilteredDataSource(data);
      setMasterDataSource(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    data();
  }, []);

  const searchFilterFunction = (text) => {
    console.log('text', text);
    if (text) {
      console.log(masterDataSource);
      const newData = masterDataSource.filter(function (item) {
        const itemData = item.common[0]
          ? item.common[0].toLowerCase()
          : ''.toLowerCase();
        const textData = text.toLowerCase();
        return itemData.indexOf(textData) > -1;
      });
      console.log('filteredData', newData);
      setFilteredDataSource(newData);
      setSearch(text);
    } else {
      setFilteredDataSource(masterDataSource);
      setSearch(text);
    }
  };
  const onBankSelected = (value) => {
    // setBankName(value);
    setFilteredDataSource([]);
  };

  return (
    <View>
      <TextInput
        round
        style={styles.searchBar}
        onChangeText={(text) => searchFilterFunction(text)}
        onClear={(text) => searchFilterFunction('')}
        placeholder="Search plant by it's common name"
        value={search}
      />
      <FlatList
        data={filteredDataSource}
        renderItem={({ item, index }) => (
          <TouchableOpacity onPress={() => onBankSelected(item?.bank)}>
            <VariantsBox>
              <Text>{item?.bank || ''}</Text>
            </VariantsBox>
          </TouchableOpacity>
        )}
        keyExtractor={(item) => item.bank}
      />
    </View>
  );
};

export default PlantSearchBar;

const styles = StyleSheet.create({
  searchBar: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
    width: 260,
  },
});
