import Slider from '@react-native-community/slider';
import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet } from 'react-native';

const IntervalSliders = ({ plant }) => {
  const [waterInterval, setWaterInterval] = useState(
    plant.wateringReminderInterval
  );
  return (
    <View style={{ paddingTop: 10 }}>
      <Text> {plant.nickName}</Text>
      <Slider
        style={{ width: 300, height: 40 }}
        minimumValue={1}
        maximumValue={15}
        minimumTrackTintColor={'rgb(151,179,128)'}
        onValueChange={(value) => {
          console.log(Math.floor(value));
          //put request);
        }}
        thumbTintColor={'rgb(151,179,128)'}
        value={plant.wateringReminderInterval}
      />
      <Text>{waterInterval}</Text>
    </View>
  );
};

export default IntervalSliders;

const styles = StyleSheet.create({});
