import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Pressable, StyleSheet } from 'react-native';
import { SearchBar } from 'react-native-elements';

function AddPlant() {
  const [nickNameText, onChangeNickNameText] = React.useState('');
  const handleChange = (possiblePlant) => {};
  const handleClear = () => {};

  return (
    <View style={styles.container}>
      <Text>Add plant to your hibernacle</Text>
      <TextInput
        placeholder="Optional plant's nickname"
        onChangeText={onChangeNickNameText}
        value={nickNameText}
        style={styles.input}
      ></TextInput>
      <SearchBar
        round
        searchIcon={{ size: 24 }}
        onChangeText={(text) => handleChange(text)}
        onClear={(text) => handleClear(text)}
        placeholder="Search by plant common name"
        // value={search}
      />
      <Pressable></Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { alignItems: 'center', justifyContent: 'center', flex: 1 },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});
export default AddPlant;
